mapper.select:
SELECT COUNT(*)
FROM users
WHERE user_id = #{id}

SELECT COUNT(*)
FROM users
WHERE phone = #{phone}

SELECT *
FROM users
WHERE user_id = #{userId}
	AND password = #{password}

SELECT user_id
FROM users
WHERE name = #{name}
	AND phone = #{phone}

SELECT password
FROM users
WHERE name = #{name}
	AND user_id = #{userId}

mapper.update:
UPDATE users
SET password = #{password}
WHERE user_id = #{userId}

UPDATE users
SET name = #{name}, password = #{password}, birthdate = #{birthdate}, address = #{address}
WHERE user_id = #{userId}
