mapper.update:
update basket
set basket_cnt = basket_cnt + #{basketCnt}
where basket_no = #{basketNo}

mapper.select:
select * from( select rownum rn, l.user_id, c.* from likegather l INNER JOIN clothes c on l.cloth_no = c.cloth_no where l.user_id = #{userId})
where rn <= (#{page} * #{maxPg})
and rn > (#{page} - 1) * #{maxPg}

select count(1)
from likegather
where user_id = #{userId}
