mapper.update:
update basket
set basket_cnt = basket_cnt + #{basketCnt}
where basket_no = #{basketNo}

mapper.select:
		select *
		from(
				select rownum rn, l.user_id, c.*
				from likegather l INNER JOIN clothes c
				on l.cloth_no = c.cloth_no
				JOIN category g
                on c.small_category = g.small_category
				where l.user_id = #{userId}
				               
				and g.big_category = #{bigCategory}
				
				)
		
		where rn <= (#{page} * #{maxPg})
		
		and rn > (#{page} - 1) * #{maxPg}

mapper.update:
update stock
set quantity = quantity - #{quantity}
where cloth_no = #{clothNo} and csize = #{csize}

mapper.select:
select *
from basket
where cloth_no = #{clothNo} and user_id = #{userId}

select *
from likegather
where cloth_no = #{clothNo} and user_id = #{userId}
